import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import {
    AuthenticationUser,
    UserPublicSession,
} from 'common/models/authentication';
import { Observable, catchError, map, of, switchMap, tap } from 'rxjs';
import { SessionService } from '../session-service/session.service';
import { Router } from '@angular/router';
import { HOME_ROUTE, LOGIN_ROUTE } from 'src/constants/routes';
import { WsService } from 'src/services/ws-service/ws.service';

@Injectable({
    providedIn: 'root',
})
export class AuthenticationService {
    constructor(
        private readonly http: HttpClient,
        private readonly sessionService: SessionService,
        private readonly router: Router,
        private readonly wsService: WsService,
    ) {}

    login(user: AuthenticationUser): Observable<UserPublicSession> {
        return this.http.post<UserPublicSession>('/auth/login', user).pipe(
            switchMap((session) =>
                this.wsService.connect(session.token).pipe(
                    tap(() => {
                        this.sessionService.setSession(session);
                        this.router.navigate([HOME_ROUTE]);
                    }),
                    map(() => session),
                ),
            ),
        );
    }

    signup(user: AuthenticationUser): Observable<UserPublicSession> {
        return this.http.post<UserPublicSession>('/auth/signup', user).pipe(
            switchMap((session) =>
                this.wsService.connect(session.token).pipe(
                    tap(() => {
                        this.sessionService.setSession(session);
                        this.router.navigate([HOME_ROUTE]);
                    }),
                    map(() => session),
                ),
            ),
        );
    }

    logout(ignoreApi: boolean = false): Observable<void> {
        const onLogout = () => {
            this.wsService.disconnect();
            this.sessionService.setSession(undefined);
            this.router.navigate([LOGIN_ROUTE]);
        };

        if (ignoreApi) {
            onLogout();
            return of();
        }

        return this.http
            .post<void>('/auth/logout', {})
            .pipe(tap(() => onLogout()));
    }

    loadSession(): Observable<UserPublicSession | undefined> {
        const token = this.sessionService.getLocalToken();

        if (!token) {
            return of(undefined);
        }

        return this.http.post<UserPublicSession>('/auth/load', { token }).pipe(
            tap((session) => {
                this.sessionService.setSession(session);
            }),
            catchError(() => of(undefined)),
        );
    }

    requestPasswordReset(email: string): Observable<void> {
        return this.http.post<void>('/auth/password-reset/request', { email });
    }

    resetPassword(token: string, newPassword: string): Observable<void> {
        return this.http.post<void>('/auth/password-reset', {
            token,
            newPassword,
        });
    }
}

import { Injectable } from '@angular/core';
import { Router } from '@angular/router';
import {
    HOME_ROUTE,
    LOGIN_ROUTE,
    PUBLICLY_ACCESSIBLE_ROUTES_PATH,
    PUBLIC_ROUTES_PATH,
} from 'src/constants/routes';
import { AuthenticationService } from 'src/modules/authentication/services/authentication-service/authentication.service';
import { StateService } from '../state-service/state.service';
import { combineLatest } from 'rxjs';
import { WsService } from '../ws-service/ws.service';
import { SessionService } from 'src/modules/authentication/services/session-service/session.service';
import { UserPublicSession } from 'common/models/authentication';
import { Location } from '@angular/common';
import {
    EMAIL_VALIDATION_TOKEN_KEY,
    EMAIL_VALIDATION_URL_REGEX,
} from 'src/constants/user-validation';
import { ValidationService } from 'src/modules/validation/services/validation.service';
import { ABOUT_ROUTE } from 'src/modules/about/constants/routes';

@Injectable({
    providedIn: 'root',
})
export class InitializerService {
    constructor(
        private readonly stateService: StateService,
        private readonly authenticationService: AuthenticationService,
        private readonly sessionService: SessionService,
        private readonly router: Router,
        private readonly location: Location,
        private readonly wsService: WsService,
        private readonly validationService: ValidationService,
    ) {}

    async initialize(): Promise<void> {
        this.stateService.setLoading();

        this.handleEmailVerificationUrl(this.location.path());

        combineLatest([
            this.sessionService.session,
            this.wsService.disconnect$,
        ]).subscribe({
            next: ([session]) => {
                if (session) {
                    this.stateService.setLoading();
                } else {
                    this.stateService.setReady();
                }
            },
            error: (error) => {
                this.stateService.setError(error);
            },
        });

        this.wsService.ws.subscribe((ws) => {
            if (ws) {
                this.stateService.setReady();
            }
        });

        this.authenticationService.loadSession().subscribe((session) => {
            if (session) {
                this.wsService.connect(session.token).subscribe();
            } else {
                this.stateService.setReady();
            }
            this.handleRedirect(session);
        });

        combineLatest([
            this.sessionService.session,
            this.wsService.ws,
        ]).subscribe(([session, ws]) => {
            if (session && ws) {
                const emailValidationToken = localStorage.getItem(
                    EMAIL_VALIDATION_TOKEN_KEY,
                );
                if (emailValidationToken) {
                    this.validationService
                        .validateEmail(emailValidationToken)
                        .subscribe();
                }

                localStorage.removeItem(EMAIL_VALIDATION_TOKEN_KEY);
            }
        });
    }

    handleRedirect(session: UserPublicSession | undefined): void {
        if (session && PUBLIC_ROUTES_PATH.includes(this.location.path())) {
            this.router.navigate([HOME_ROUTE]);
        }

        if (!session && !this.isPubliclyAccessibleRoute(this.location.path())) {
            this.router.navigate([LOGIN_ROUTE]);
        }
    }

    private handleEmailVerificationUrl(path: string): void {
        const match = EMAIL_VALIDATION_URL_REGEX.exec(path);

        const token = match?.groups?.['token'];

        if (!token) return;

        localStorage.setItem(EMAIL_VALIDATION_TOKEN_KEY, token);
    }

    private isPubliclyAccessibleRoute(path: string): boolean {
        return (
            path.startsWith(ABOUT_ROUTE) ||
            PUBLICLY_ACCESSIBLE_ROUTES_PATH.find((publicPath) =>
                new RegExp(`^${publicPath}(?:\\?.+)?$`).test(path),
            ) !== undefined
        );
    }
}

import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { MatDialog } from '@angular/material/dialog';
import { WsServer } from 'common/models/ws';
import { WsService } from 'src/services/ws-service/ws.service';
import { PublicProfileService } from '../public-profile-service/public-profile.service';
import { map, switchMap } from 'rxjs';
import { ModalService } from 'src/modules/modals/services/modal.service';
import { MatSnackBar } from '@angular/material/snack-bar';
import { PublicUserResultClass } from '../../models/public-user-result';

@Injectable({
    providedIn: 'root',
})
export class MatchingService {
    constructor(
        private readonly http: HttpClient,
        private readonly wsService: WsService,
        private readonly dialog: MatDialog,
        private readonly publicProfileService: PublicProfileService,
        private readonly modalService: ModalService,
        private readonly snackBar: MatSnackBar,
    ) {
        this.instantiate();
    }

    likeUser(userId: number) {
        return this.http.post(`/matching/like/${userId}`, {}).pipe(
            switchMap(() => this.publicProfileService.fetchMatches()),
            map(() => {}),
        );
    }

    dislikeUser(userId: number) {
        return this.http.post(`/matching/dislike/${userId}`, {}).pipe(
            switchMap(() => this.publicProfileService.fetchMatches()),
            map(() => {}),
        );
    }

    unmatchedUser(userId: number) {
        return this.http.post(`/matching/unmatch/${userId}`, {}).pipe(
            switchMap(() => this.publicProfileService.fetchMatches()),
            map(() => {}),
        );
    }

    askUnmatchUser(user: PublicUserResultClass) {
        this.modalService.open({
            title: `Supprimer ${user.currentValue.name} de vos matchs ?`,
            content: `Vous ne pourrez plus lui envoyer de messages.`,
            buttons: [
                {
                    content: 'Annuler',
                    closeDialog: true,
                },
                {
                    content: 'Supprimer',
                    closeDialog: true,
                    color: true,
                    action: () => this.unmatchedUser(user.id).subscribe(),
                },
            ],
        });
    }

    private instantiate() {
        this.wsService
            .listen('match:matched-active')
            .subscribe((data) => this.handleMatch(data, true));
        this.wsService
            .listen('match:matched-passive')
            .subscribe((data) => this.handleMatch(data, false));
    }

    private handleMatch(
        {
            matchedUserId,
        }: WsServer['match:matched-active' | 'match:matched-passive'],
        active: boolean,
    ) {
        this.publicProfileService.fetchMatches().subscribe((result) => {
            const user = result.find((user) => user.id === matchedUserId);

            if (!user) {
                throw new Error('User not found');
            }

            user.loaded.subscribe((loaded) => {
                if (loaded) {
                    if (active) {
                        this.modalService.open({
                            title: "C'est un match !",
                            content: `Vous avez match avec ${user.currentValue.name} !`,
                            buttons: [
                                {
                                    content: 'Plus tard',
                                    closeDialog: true,
                                },
                                {
                                    content: 'Discuter',
                                    redirect: `/matches/${user.id}`,
                                    color: true,
                                },
                            ],
                        });
                    } else {
                        this.snackBar.open(
                            `Nouveau match avec ${user.currentValue.name} !`,
                            undefined,
                            { duration: 2000, verticalPosition: 'top' },
                        );
                    }
                }
            });
        });
    }
}

import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable, of, switchMap, tap } from 'rxjs';
import { TypeOfId } from 'common/types/id';
import { User } from 'common/models/user';
import { Message } from 'common/models/message';
import { HttpClient } from '@angular/common/http';
import { SessionService } from 'src/modules/authentication/services/session-service/session.service';
import { WsService } from 'src/services/ws-service/ws.service';

@Injectable({
    providedIn: 'root',
})
export class MessagesService {
    private messages$: BehaviorSubject<Map<TypeOfId<User>, Message[]>> =
        new BehaviorSubject(new Map());

    constructor(
        private readonly http: HttpClient,
        private readonly sessionService: SessionService,
        private readonly wsService: WsService,
    ) {
        this.wsService.listen('message:new').subscribe((message) => {
            this.messages$.next(
                this.messages$.value.set(message.senderId, [
                    ...(this.messages$.value.get(message.senderId) ?? []),
                    message,
                ]),
            );
        });

        this.sessionService.isLoggedIn().subscribe((isLoggedIn) => {
            if (!isLoggedIn) this.messages$.next(new Map());
        });
    }

    getMessages(userId: TypeOfId<User>): Observable<Message[]> {
        return this.messages$.pipe(
            switchMap((messages) =>
                messages.has(userId)
                    ? of(messages.get(userId)!)
                    : this.fetchMessages(userId),
            ),
        );
    }

    sendMessage(userId: TypeOfId<User>, content: string): Observable<void> {
        this.messages$.next(
            this.messages$.value.set(userId, [
                ...(this.messages$.value.get(userId) ?? []),
                {
                    content,
                    recipientId: userId,
                    senderId:
                        this.sessionService.getCurrentSession().user.userId,
                    timestamp: new Date(),
                    read: false,
                },
            ]),
        );

        return this.http.post<void>('/messages', {
            recipientId: userId,
            content,
        });
    }

    markAsRead(userId: TypeOfId<User>): Observable<void> {
        return this.http.post<void>(`/messages/read/${userId}`, {});
    }

    private fetchMessages(userId: TypeOfId<User>): Observable<Message[]> {
        return this.http.get<Message[]>(`/messages/${userId}`).pipe(
            tap((messages) => {
                this.messages$.next(this.messages$.value.set(userId, messages));
            }),
        );
    }
}

