import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { MatSnackBar } from '@angular/material/snack-bar';
import { UserValidationResponse } from 'common/models/user';
import {
    BehaviorSubject,
    Observable,
    catchError,
    map,
    of,
    switchMap,
    tap,
} from 'rxjs';
import { SessionService } from 'src/modules/authentication/services/session-service/session.service';
import { WsService } from 'src/services/ws-service/ws.service';

@Injectable({
    providedIn: 'root',
})
export class ValidationService {
    private userValid$: BehaviorSubject<UserValidationResponse | undefined> =
        new BehaviorSubject<UserValidationResponse | undefined>(undefined);

    constructor(
        private readonly wsService: WsService,
        private readonly http: HttpClient,
        private readonly sessionService: SessionService,
        private readonly snackBar: MatSnackBar,
    ) {
        this.wsService
            .listen('user-validation:update')
            .subscribe((userValid) => {
                this.userValid$.next(userValid);
            });

        this.sessionService.isLoggedIn().subscribe((isLoggedIn) => {
            if (isLoggedIn) {
                this.fetchValidation().subscribe();
            } else {
                this.userValid$.next(undefined);
            }
        });
    }

    get userValid(): Observable<boolean | undefined> {
        return this.userValid$.pipe(
            map((userValid) =>
                userValid
                    ? Boolean(
                          userValid.emailValidated &&
                              userValid.userProfileReady,
                      )
                    : undefined,
            ),
        );
    }

    get userValidActionsCount(): Observable<number | undefined> {
        return this.userValid$.pipe(
            map((userValid) =>
                userValid
                    ? (userValid.emailValidated ? 0 : 1) +
                      (userValid.userProfileReady ? 0 : 1)
                    : undefined,
            ),
        );
    }

    get userValidMessage(): Observable<string | undefined> {
        return this.userValid$.pipe(
            map((userValid) => {
                if (!userValid) return undefined;

                if (!userValid.emailValidated) {
                    return 'Veuillez vrifier votre email';
                }

                if (!userValid.userProfileReady) {
                    return 'Veuillez complter votre profil';
                }

                return undefined;
            }),
        );
    }

    get userProfileReady(): Observable<boolean | undefined> {
        return this.userValid$.pipe(
            map((userValid) =>
                userValid ? Boolean(userValid.userProfileReady) : undefined,
            ),
        );
    }

    get emailValidated(): Observable<boolean | undefined> {
        return this.userValid$.pipe(
            map((userValid) =>
                userValid ? Boolean(userValid.emailValidated) : undefined,
            ),
        );
    }

    validateEmail(token: string): Observable<UserValidationResponse> {
        return this.http.post<void>('/user-validation/email', { token }).pipe(
            map(() =>
                this.snackBar.open('Email vrifi !', 'OK', { duration: 5000 }),
            ),
            catchError(() => {
                this.snackBar.open(
                    'Erreur lors de la vrification du email',
                    'OK',
                    { duration: 5000 },
                );
                return of(undefined);
            }),
            switchMap(() => this.fetchValidation()),
        );
    }

    requestEmailValidation(): Observable<UserValidationResponse> {
        return this.http.post<void>('/user-validation/email/request', {}).pipe(
            map(() =>
                this.snackBar.open('Email de vrification envoy !', 'OK', {
                    duration: 5000,
                }),
            ),
            catchError(() => {
                this.snackBar.open(
                    "Erreur lors de l'envoi du email de vrification",
                    'OK',
                    { duration: 5000 },
                );
                return of(undefined);
            }),
            switchMap(() => this.fetchValidation()),
        );
    }

    private fetchValidation(): Observable<UserValidationResponse> {
        return this.http
            .get<UserValidationResponse>('/user-validation')
            .pipe(tap((userValid) => this.userValid$.next(userValid)));
    }
}

import { Injectable } from '@angular/core';
import { io, Socket } from 'socket.io-client';
import { environment } from 'src/environments/environment';
import { WsClient, WsServer } from 'common/models/ws';
import { BehaviorSubject, map, Observable, Subject, switchMap } from 'rxjs';

@Injectable({
    providedIn: 'root',
})
export class WsService {
    private ws$: BehaviorSubject<Socket<WsServer, WsClient> | undefined> =
        new BehaviorSubject<Socket<WsServer, WsClient> | undefined>(undefined);
    readonly disconnect$ = new Subject<void>();

    constructor() {}

    connect(token: string): Observable<Socket<WsServer, WsClient> | undefined> {
        try {
            const ws: Socket<WsServer, WsClient> = io(environment.api.url, {
                transports: ['websocket'],
                upgrade: false,
                auth: { token },
            });

            ws.on('connect', () => {
                this.ws$.next(ws);
            });

            ws.on('disconnect', () => {
                this.ws$.next(undefined);
                this.disconnect$.next();
            });

            ws.on('connect_error', (error) => {
                // eslint-disable-next-line no-console
                console.error('WebSocket error', error);
            });
        } catch (e) {
            this.ws$.error(e);
        }

        return this.ws$.asObservable();
    }

    disconnect() {
        this.ws$.value?.disconnect();
        this.ws$.next(undefined);
        this.disconnect$.next();
    }

    get ws() {
        return this.ws$.asObservable();
    }

    get connected() {
        return this.ws.pipe(map((ws) => !!ws));
    }

    listen<T extends keyof WsServer>(event: T): Observable<WsServer[T]> {
        return this.ws$.pipe(
            switchMap((ws) => {
                const subject = new Subject<WsServer[T]>();

                // eslint-disable-next-line @typescript-eslint/no-explicit-any
                ws?.on(event as any, (data: WsServer[T]) => {
                    subject.next(data);
                });

                return subject.asObservable();
            }),
        );
    }

    next<T extends keyof WsClient>(event: T, data: WsClient[T]) {
        if (!this.ws$.value) throw new Error('WS not connected');
        // eslint-disable-next-line @typescript-eslint/no-explicit-any
        this.ws$.value.emit(event as any, data);
    }
}

