src/modules/matching/utils/messages.ts.defaultValue:
(messages: Message[]): MessageGroup[] => {
    const groupedMessages: MessageGroup[] = [];
    let currentGroup: MessageGroup | undefined = undefined;
    let previousMessage: Message | undefined = undefined;

    for (const message of messages) {
        message.timestamp = new Date(message.timestamp);

        if (!previousMessage || !isSameGroup(message, previousMessage)) {
            currentGroup = {
                timestamp: message.timestamp,
                senderId: message.senderId,
                messages: [],
            };
            groupedMessages.push(currentGroup);
        }

        currentGroup!.messages.push(message);

        previousMessage = message;
    }

    return groupedMessages;
}

(message: Message, previousMessage: Message): boolean => {
    return (
        message.senderId === previousMessage.senderId &&
        message.timestamp.getTime() - previousMessage.timestamp.getTime() <
            5 * 60 * 1000
    );
}

