src/guards/private-route/private-route.g.defaultValue:
() => {
    const router = inject(Router);
    const sessionService = inject(SessionService);
    const stateService = inject(StateService);

    return combineLatest([
        sessionService.isLoggedIn(),
        stateService.state,
    ]).pipe(
        map(([isLoggedIn, state]) => {
            if (state !== State.READY) {
                return true;
            }

            if (!isLoggedIn) {
                router.navigate([LOGIN_ROUTE]);
            }

            return isLoggedIn;
        }),
    );
}

src/guards/public-route/public-route.gua.defaultValue:
() => {
    const router = inject(Router);
    const sessionService = inject(SessionService);
    const stateService = inject(StateService);

    return combineLatest([
        sessionService.isLoggedIn(),
        stateService.state,
    ]).pipe(
        map(([isLoggedIn, state]) => {
            if (state !== State.READY) {
                return true;
            }

            if (isLoggedIn) {
                router.navigate([HOME_ROUTE]);
            }

            return !isLoggedIn;
        }),
    );
}

